query GetUserBadges($user: Bytes!) {
  badges {
    badgeId
    uri
    badgeTiers {
      points
      tier
      uri
    }
  }
  accountBadges(where: {user: $user}) {
    points
    tier
    badge {
      badgeId
      uri
      badgeTiers {
        points
        tier
        uri
      }
    }
  }
}
